mapper.update:
UPDATE	/*[departmentsDao.xml][doSelectDepartment]*/
		EMPLOYEES
SET		DEPARTMENT_ID = #{departmentId}
WHERE	EMPLOYEES_ID = #{employeeId}

mapper.insert:
		INSERT INTO /*[departmentsDao.xml][doAddNewDepartment]*/
			DEPARTMENTS (
  						 DEPARTMENT_ID, DEPARTMENT_NAME
  						 ) 
		VALUES 			( DEPARTMENT_ID_SEQ.NEXTVAL,
 							#{departmentName} 
 						)
